function MenuScrollAnchors(e,t){const o=document.querySelectorAll(t);for(let e of o)e.addEventListener("click",function(t){t.preventDefault();const o=e.getAttribute("href");document.querySelector(o).scrollIntoView({behavior:"smooth",block:"start"})});let n=[];for(let t of document.querySelectorAll(e))n.push(t.getBoundingClientRect().top+pageYOffset+window.innerHeight/3*2-60);window.scrollBy(0,1);let c=-1;window.addEventListener("scroll",function(){for(let e=0;e<n.length;e++)if(window.pageYOffset<=n[e]){c!==e&&(document.querySelector(t+".active")&&document.querySelector(t+".active").classList.remove("active"),o[c=e].classList.add("active"));break}})}